-- Module 1: Introduction to Budgeting
INSERT INTO quizzes (module_id, title, passing_score, time_limit, questions) VALUES (1, 'Introduction to Budgeting Quiz', 70, 20, '[
  {"question": "What is a budget?", "options": ["A plan for spending and saving money", "A type of bank account", "A credit card", "A loan"], "correctAnswer": 0},
  {"question": "Why is budgeting important?", "options": ["It helps track income and expenses", "It increases your salary", "It eliminates all debt", "It guarantees wealth"], "correctAnswer": 0},
  {"question": "What are fixed expenses?", "options": ["Expenses that stay the same each month", "Expenses that change monthly", "One-time purchases", "Entertainment costs"], "correctAnswer": 0},
  {"question": "Which is an example of a variable expense?", "options": ["Groceries", "Rent", "Insurance", "Loan payment"], "correctAnswer": 0},
  {"question": "What is the 50/30/20 budgeting rule?", "options": ["50% needs, 30% wants, 20% savings", "50% savings, 30% needs, 20% wants", "50% wants, 30% savings, 20% needs", "Equal distribution"], "correctAnswer": 0},
  {"question": "How often should you review your budget?", "options": ["Monthly", "Once a year", "Never", "Every 5 years"], "correctAnswer": 0},
  {"question": "What is income?", "options": ["Money you earn or receive", "Money you spend", "Money you owe", "Money you save"], "correctAnswer": 0},
  {"question": "Which tool can help track your budget?", "options": ["Spreadsheet or budgeting app", "Credit card", "Loan document", "Receipt only"], "correctAnswer": 0},
  {"question": "What should you do if expenses exceed income?", "options": ["Cut unnecessary expenses", "Ignore it", "Borrow more money", "Stop budgeting"], "correctAnswer": 0},
  {"question": "What is the first step in creating a budget?", "options": ["List all sources of income", "Buy expensive items", "Apply for loans", "Close bank accounts"], "correctAnswer": 0}
]');

-- Module 2: Smart Saving Tips
INSERT INTO quizzes (module_id, title, passing_score, time_limit, questions) VALUES (2, 'Smart Saving Tips Quiz', 70, 20, '[
  {"question": "What is an emergency fund?", "options": ["Money saved for unexpected expenses", "Money for vacations", "Money for shopping", "Money for investments"], "correctAnswer": 0},
  {"question": "How much should you save in an emergency fund?", "options": ["3-6 months of expenses", "1 week of expenses", "1 year of income", "No specific amount"], "correctAnswer": 0},
  {"question": "What is the pay yourself first method?", "options": ["Save money before spending on other things", "Pay bills first", "Spend first, save later", "Never save"], "correctAnswer": 0},
  {"question": "Which is a good saving habit?", "options": ["Automatic transfers to savings", "Spending all income", "Avoiding banks", "Only saving once a year"], "correctAnswer": 0},
  {"question": "What is compound interest?", "options": ["Interest earned on both principal and accumulated interest", "Simple interest only", "Bank fees", "Loan charges"], "correctAnswer": 0},
  {"question": "Where is a safe place to keep savings?", "options": ["Bank savings account", "Under the mattress", "In a wallet", "With friends"], "correctAnswer": 0},
  {"question": "What percentage of income should you aim to save?", "options": ["At least 10-20%", "0%", "100%", "5% only"], "correctAnswer": 0},
  {"question": "What is a savings goal?", "options": ["A specific amount you want to save for a purpose", "Any random amount", "Money you owe", "Your monthly income"], "correctAnswer": 0},
  {"question": "How can you reduce expenses to save more?", "options": ["Cut unnecessary spending", "Earn less", "Borrow more", "Ignore expenses"], "correctAnswer": 0},
  {"question": "What is the benefit of saving regularly?", "options": ["Builds financial security over time", "Increases debt", "Reduces income", "No benefits"], "correctAnswer": 0}
]');

-- Module 3: Understanding Credit
INSERT INTO quizzes (module_id, title, passing_score, time_limit, questions) VALUES (3, 'Understanding Credit Quiz', 70, 20, '[
